import React, {useState} from 'react';
import QrReader from 'react-qr-reader';
import classes from './qr-reder.module.scss';

const QRCodeReader = () => {

    const [qrcode, setQrcode] = useState(null);

    const handleError = (err) => {

        console.log('QRCode error ', err);

        // setQrcode(null);
    }

    const handleScan = (data) => {

        console.log('QRCode scan ', data);

        if(data){

            const mac = data.substring(data.lastIndexOf('/')+1);
            setQrcode(mac);
        }
    }

    return (
        <div>
            <QrReader
            delay={100}
            onError={handleError}
            onScan={handleScan}
            style={{ width: '30%' }}
            resolution={1000}
            />
            {
                qrcode?
                <div>
                    <p>{qrcode}</p>
                </div>
                :
                null
            }
        </div>
    );
}

export default QRCodeReader;
