import React, {useState} from 'react';
import QrReader from 'react-qr-reader';
import classes from './qr-reder.module.scss';

class QRCodeReader extends React.Component {
  state = {
    result: null
  }

  handleScan = data => {
    if (data) {
      console.log(data);
      this.setState({
        result: data
      })
    }
  }
  handleError = err => {
    console.error(err)
    this.setState(null);
  }
  render() {
    return (
      <div>
        <QrReader
          delay={300}
          onError={this.handleError}
          onScan={this.handleScan}
          style={{ width: '100%' }}
        />
        <p>{this.state.result}</p>
      </div>
    )
  }
}

export default QRCodeReader;
