import React from 'react';
// import { action } from '@storybook/addon-actions';
import {Button, Icon, Label} from 'semantic-ui-react';

export default {
    title:'Semantic button',
    component: Button
}

export const Text = () => <Button>Click Me</Button>

export const ShortHandText = () => <Button content='Click Me' />

export const Emphasis = () => (
    <div>
        <Button primary>Click Me</Button>
        <Button secondary>Click Me</Button>
    </div>
);

export const Color = () => (
    <div>
        <Button content='Color' color='red' />
    </div>
);

export const WithLabel = () => (
    <Button as='div' labelPosition='right'>
      <Button icon>
        <Icon name='heart' />
        Like
      </Button>
      <Label as='a' basic pointing='left'>
        2,048
      </Label>
    </Button>
);

export const shortHandWithLabel = () => (
    <Button
      color='blue'
      content='Like'
      icon='heart'
      label={{ as: 'a', basic: true, content: '2,048', color:'red' }}
      labelPosition='right'
    />
);

export const renderAS = () => (
    <Button as='div'>
        div element
    </Button>
);

export const IconButton = () => <Button icon='world' />

export const IconLabel = () => (
    <div>
        <Button content='Pause' icon='pause' labelPosition='left' />
        <Button content='Next' icon='right arrow' labelPosition='right' />
    </div>
);

export const IconLabelAnimated = () => (
    <div>
        <Button animated='fade'>
            <Button.Content visible content='Pause'  />
            <Button.Content hidden>
                <Icon name='pause' />
            </Button.Content>
        </Button>
    </div>
);

export const Inverted = () => (
    <Button inverted color='blue' icon='pause' />
);

export const Group = () => (
    <Button.Group basic color='pink' buttons={['One', 'Two', 'Three']} />
);

export const IconGroup = () => (
    <div>
        <Button.Group
        buttons={[
            { key: 'align left', icon: 'align left' },
            { key: 'align center', icon: 'align center' },
            { key: 'align right', icon: 'align right' },
            { key: 'align justify', icon: 'align justify' },
        ]}
        />{' '}
        <Button.Group
        buttons={[
            { key: 'bold', icon: 'bold' },
            { key: 'underline', icon: 'underline' },
            { key: 'text width', icon: 'text width' },
        ]}
        />
    </div>
);

export const condition = () => (
    <div>
        <Button.Group basic>
            <Button.Group
            buttons={[
                { key: 'align left', icon: 'align left' },
                { key: 'align center', icon: 'align center' },
                { key: 'align right', icon: 'align right' },
                { key: 'align justify', icon: 'align justify' },
            ]}
            />
            <Button.Or as='div' text='or' />
            <Button.Group
            buttons={[
                { key: 'bold', icon: 'bold', active:true },
                { key: 'underline', icon: 'underline' },
                { key: 'text width', icon: 'text width' },
            ]}
            />
        </Button.Group>
    </div>
);

export const SubtleColor = () => (
    <div>
        <Button basic color='purple' animated='fade'>
            <Button.Content visible content='Pause'  />
            <Button.Content hidden>
                <Icon name='pause' />
            </Button.Content>
        </Button>
    </div>
);

export const Animated = () => (
    <div>
        <Button animated>
            <Button.Content visible>Next</Button.Content>
            <Button.Content hidden>
                <Icon name='arrow right' />
            </Button.Content>
        </Button>
        <Button animated='vertical'>
            <Button.Content hidden>Shop</Button.Content>
            <Button.Content visible>
                <Icon name='shop' />
            </Button.Content>
        </Button>
        <Button animated='fade'>
            <Button.Content visible>Sign-up for a Pro account</Button.Content>
            <Button.Content hidden>$12.99 a month</Button.Content>
        </Button>
    </div>
);
